// Reducer function
function dialogReducer(state, action) {
  switch (action.type) {
    case 'SET_VALUTATE':
      return {...state, valutate: action.payload};
    case 'SET_MONEY':
      return {...state, money: action.payload};
    case 'SET_COUNTS':
      return {...state, counts: action.payload};
    case 'SET_MESSAGE':
      return {...state, message: action.payload};
    case 'SET_BANK':
      return {...state, banker: action.payload};
    case 'SET_MESSAGES':
      return {...state, messages: action.payload};
    case 'ADD_MESSAGE':
      return {...state, messages: [...state.messages, action.payload]};
    case 'SET_INPUT':
      return {...state, input: action.payload};
    case 'SET_COUNTRY':
      return {...state, country: action.payload};
    case 'UPDATE_POINT':
      return {
        ...state,
        point: {
          ...state.point,
          [action.payload.key]: state.point[action.payload.key] + action.payload.value
        }
      };
    case 'RESET_POINT':
      return {
        ...state,
        point: {
          Balance: 0,
          Plus: 0,
          Money: 0,
          Kopit: 0,
          Big: 0,
          Vozmozh: 0,
        }
      };
    case 'CLEAR_MESSAGES':
      return {
        ...state,
        messages: [
          {
            sender: 'bot-message',
            component: (
              <Buttoniany
                first="üëã –ü–æ–∫–∞, –µ—Å–ª–∏ –Ω—É–∂–Ω–∞ –±—É–¥–µ—Ç –ø–æ–º–æ—â—å, —è —Ç—É—Ç"
                buttons={['üí∏ –ö–æ–º–∏—Å—Å–∏—è', 'üí± –û–±–º–µ–Ω –≤–∞–ª—é—Ç—ã', 'üè¶ –í–∫–ª–∞–¥—ã –∏ —Å—á–µ—Ç–∞']}
                onClickHandler={[comissia, trade_valuta, vkladiandscheta]}
              />
            )
          }
        ]
      };
    default:
      return state;
  }
}

