    addMessage({sender: 'user-message', text: vote});

    if (vote === '1') {
      updatePoint('Big', 1);
    } else if (vote === '2') {
      updatePoint('Balance', 1);
    } else if (vote === '3') {
      updatePoint('Plus', 1);
    } else if (vote === '4') {
      updatePoint('Money', 1);
    } else {
      updatePoint('Kopit', 1);
    }

    const maxValue = Math.max(...Object.values(point));
    const itog = [];

    for (const key in point) {
      if (point[key] === maxValue) {
        itog.push(key);
      }
    }

    const user_itog = itog.map(key => pointnames[key]);

    addMessage({
      sender: 'bot-message', component: (
        <>
          {user_itog.map((key, itogi) => (
            <p key={itogi}>–¢–µ–±–µ –ø–æ–¥—Ö–æ–¥—è—Ç —Ç–∞–∫–∏–µ –≤–∫–ª–∞–¥—ã –∫–∞–∫: {key}</p>
          ))}
        </>
      )
    });
  };

  const categories = (text) => {
    addMessage({sender: 'user-message', text: text});
    addMessage({
      sender: 'bot-message', component: (
        <>
          <p>–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –≤ –Ω–∞—à–µ–º –±–∞–Ω–∫–µ –≤—ã –º–æ–∂–µ—Ç–µ —É–≤–∏–¥–µ—Ç—å –ø–æ —Å—Å—ã–ª–∫–µ üëâ </p>
          <a href="https://www.gazprombank.ru/personal/increase/deposits/">https://www.gazprombank.ru/personal/increase/deposits/</a>
        </>
      )
    });
  };

  const privileges = (text) => {
    addMessage({sender: 'user-message', text: text});
    addMessage({
      sender: 'bot-message', component: <Deposit/>
    });
  };

  const trade_valuta = () => {
    addMessage({sender: 'user-message', text: '–û–±–º–µ–Ω –≤–∞–ª—é—Ç—ã'});
    addMessage({
      sender: 'change-moneta', component: <TradeCurrency/>
    });
    addMessage({
      sender: 'second-bot-message', component: (
        <Buttoniany
          first="üëÄ –ú–æ–∂–µ—Ç —Ç–µ–ø–µ—Ä—å –Ω—É–∂–Ω–∞ –ø–æ–º–æ—â—å —Å —á–µ–º-—Ç–æ –¥—Ä—É–≥–∏–º?"
          buttons={['üí∏ –ö–æ–º–∏—Å—Å–∏—è', 'üí± –û–±–º–µ–Ω –≤–∞–ª—é—Ç—ã', 'üè¶ –í–∫–ª–∞–¥—ã –∏ —Å—á–µ—Ç–∞']}
          onClickHandler={[comissia, trade_valuta, vkladiandscheta]}
        />
      )
    });
  };

  const valuta = (country) => {
    setCountry(country);
    addMessage({sender: 'user-message', text: country});
    addMessage({sender: 'bot-message', text: '‚úçÔ∏è –ù–∞–ø–∏—à–∏ —Å—É–º–º—É –∫–æ—Ç–æ—Ä—É—é —Ç—ã —Ö–æ—á–µ—à—å –ø–µ—Ä–µ–≤–µ—Å—Ç–∏'});
  };

  const Sendtoback = async (data) => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/", data, {
        headers: {"Content-Type": "application/json"},
      });

      addMessage({
        sender: 'bot-message', component: (
          <>
            <p>–í–∞—à–∞ –∫–æ–º–∏—Å—Å–∏—è —Å–æ—Å—Ç–∞–≤–∏—Ç: undefined </p>
          </>
        )
      });

      console.log("–£—Å–ø–µ—à–Ω—ã–π –æ—Ç–≤–µ—Ç –∏ –¥–∞–Ω–Ω—ã–µ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω—ã", response.data);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ –¥–∞–Ω–Ω—ã—Ö:", error);
    }
  };

  const bank = (bankik, summa) => {
    setBank(bankik);
    addMessage({sender: 'user-message', text: bankik});
    addMessage({
      sender: 'bot-message',
      component: (
        <>
          <h3>‚úÖ –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–µ—Ä–µ–≤–æ–¥–µ</h3>
          <p>üíµ –í–∞–ª—é—Ç–∞: {valutate}</p>
          <p>üåç –°—Ç—Ä–∞–Ω–∞: {country}</p>
          <p>üí∞ –°—É–º–º–∞: {summa}</p>
          <p> üè¶ –ë–∞–Ω–∫: {bankik}</p>
          <button className="button" onClick={() => Sendtoback({valutate, summa, bankik, country})}>
            –í—Å–µ –≤–µ—Ä–Ω–æ
          </button>
        </>
      ),
    });
  };

  const bankers = (msg) => {
    addMessage({
      sender: 'bot-message',
      component: (
        <>
          <p>–í—ã–±–µ—Ä–∏ –±–∞–Ω–∫ –≤ –∫–æ—Ç–æ—Ä—ã–π —Ö–æ—á–µ—à—å —Å–¥–µ–ª–∞—Ç—å –ø–µ—Ä–µ–≤–æ–¥</p>
          <Export func={bank} choose={0} val2={msg}/>
        </>
      ),
    });
  };

  const handleSend = (e) => {
    e.preventDefault();
    const trimmed = input.trim();
    if (!trimmed) return;

    addMessage({sender: 'user-message', text: trimmed});

    if (valutate && !message) {
      const msg = trimmed;
      if (!isNaN(msg) && isFinite(msg) && Number(msg) > 0) {
        if (valutate && !message) {
          setMessage(msg);
          bankers(msg);
        }
      } else {
        addMessage({sender: 'bot-message', text: '–¢—ã –≤–≤–µ–ª –Ω–µ–≤–µ—Ä–Ω–æ–µ —á–∏—Å–ª–æ'});
      }
    } else {
      const lastComponent = [...messages].reverse().find(m => m.component)?.component;

      addMessage({sender: 'bot-message', text: 'üò© –ü—Ä–æ—Å—Ç–∏, –ø–æ–∫–∞ –∑–∞—Ç—Ä—É–¥–Ω—è—é—Å—å –æ—Ç–≤–µ—Ç–∏—Ç—å –Ω–∞ —Ç–≤–æ–π –≤–æ–ø—Ä–æ—Å'});

      if (lastComponent) {
        addMessage({sender: 'second-bot-message', component: lastComponent});
      }
    }

    setInput("");
  };

  return (
    <div>
      <Smska
        input={input}
        handleSend={handleSend}
        setInput={setInput}
        messages={messages}
        clear={clearMessages}
      />
    </div>
  );
}
