            console.log("–£—Å–ø–µ—à–Ω—ã–π –æ—Ç–≤–µ—Ç –∏ –¥–∞–Ω–Ω—ã–µ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω—ã", response.data);
        } catch (error) {
            console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ –¥–∞–Ω–Ω—ã—Ö:", error);
        }
    };

    const bank = (bankik, summa) => {
        setBank(bankik);
        setMessages(prev => [
            ...prev,
            {sender: 'user-message', text: bankik},
            {
                sender: 'bot-message',
                component: (
                    <>
                        <h3>‚úÖ –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–µ—Ä–µ–≤–æ–¥–µ</h3>
                        <p>üíµ –í–∞–ª—é—Ç–∞: {valutate}</p>
                        <p>üåç –°—Ç—Ä–∞–Ω–∞: {country}</p>
                        <p>üí∞ –°—É–º–º–∞: {summa}</p>
                        <p> üè¶ –ë–∞–Ω–∫: {bankik}</p>
                        <button className="button" onClick={() => Sendtoback({valutate, summa, bank: bankik, country})}>
                            –í—Å–µ –≤–µ—Ä–Ω–æ
                        </button>
                    </>

                ),
            },
        ]);
    };
    const handleSend = (e) => {
        e.preventDefault();
        const trimmed = input.trim();
        if (!trimmed) return;
        setMessages(prev => [...prev, {sender: 'user-message', text: trimmed}]);
        if (valutate && !message) {
            const msg = trimmed;
            if (!isNaN(msg) && isFinite(msg) && Number(msg) > 0) {
                if (valutate && !message) {
                    setMessage(msg);
                    setMessages(prev => [
                        ...prev,
                        {
                            sender: 'bot-message',
                            component: (
                                <ButtonsForAccept
                                    first={'–í—ã–±–µ—Ä–∏ –±–∞–Ω–∫'}
                                    buttons={['–°–±–µ—Ä–±–∞–Ω–∫', '–¢-–±–∞–Ω–∫', '–°–æ–≤–∫–æ–º–±–∞–Ω–∫']}
                                    onClickHandler={bank}
                                    sums={msg}
                                />
                            ),
                        },
                    ]);
                }
            } else {
                setMessages(prev => [
                    ...prev,
                    {sender: 'bot-message', text: '–¢—ã –≤–≤–µ–ª –Ω–µ–≤–µ—Ä–Ω–æ–µ —á–∏—Å–ª–æ'},
                ]);
            }
        } else {
            setMessages(prev => {
                const lastComponent = [...prev].reverse().find(m => m.component)?.component;
                return [
                    ...prev,
                    {sender: 'bot-message', text: 'üò© –ü—Ä–æ—Å—Ç–∏, –ø–æ–∫–∞ –∑–∞—Ç—Ä—É–¥–Ω—è—é—Å—å –æ—Ç–≤–µ—Ç–∏—Ç—å –Ω–∞ —Ç–≤–æ–π –≤–æ–ø—Ä–æ—Å'},
                    lastComponent && {sender: 'second-bot-message', component: lastComponent}
                ].filter(Boolean);
            });
        }
        setInput("");
    };
    return (
        <div>
            <Smska
                input={input}
                handleSend={handleSend}
                setInput={setInput}
                messages={messages}
                clear={handleClearMessages}
            />

        </div>
    );
}
